mapper.select:
select
id, project_id, epic, report_to, feature_status, version, description, close_date, author_name, create_time,
title, user_id, update_time,moudle
from oneclick.feature

    user_id = #{userId}
    and project_id = #{projectId}

        and report_to = #{reportTo}


        and status = #{status}


        and version = #{version}


        and close_date > #{closeDateBegin}
        and close_date < #{closeDateEnd}


        and author_name = #{authorName}


        and create_time > #{createTimeBegin}
        and create_time < #{createTimeEnd}


        and title = #{title}




        ${filter}

select id,
title
from feature

    project_id = #{projectId}
    and user_id = #{masterId}

        and title like concat('%',#{title},'%')

select
id, project_id, epic, report_to, feature_status, version, description, close_date, author_name, create_time,
title, user_id, update_time,moudle
from oneclick.feature

    user_id = #{userId}
    and project_id = #{projectId}

        and report_to = #{reportTo}


        and status = #{status}


        and version = #{version}


        and close_date > #{closeDateBegin}
        and close_date < #{closeDateEnd}


        and author_name = #{authorName}


        and create_time > #{createTimeBegin}
        and create_time < #{createTimeEnd}


        and title = #{title}




        ${filter}

SELECT id, title FROM feature WHERE project_id = #{projectId}
AND title LIKE CONCAT('%', #{title}, '%')
ORDER BY title
